import MapGL, {Marker, Popup, NavigationControl} from 'react-map-gl';
import Pin from './pin'
const TOKEN = 'pk.eyJ1IjoiYnJpY2V6YWtyYSIsImEiOiJjazJwbmR3bmcwNjRmM25wZ2VmaTM3MHJ0In0.5F4SAt1Rtc-MXzjv6wxWaQ'; // Set your mapbox token here
const navStyle = {
  position: 'absolute',
  top: 0,
  left: 0,
  padding: '10px'
};

export default class Map extends Component {constructor(props) {
    super(props);
    this.state = {
      viewport: {
        latitude: 37.785164,
        longitude: -100,
        zoom: 2.8,
        bearing: 0,
        pitch: 0,
        width: 500,
        height: 500,
      },
      popupInfo: null
    };
    this.renderPopup = this.renderPopup.bind(this)
  }renderPopup(){
    return this.state.popupInfo && (
      <Popup tipSize={5}
        anchor="bottom-right"
        longitude={this.state.popupInfo.state.longitude}
        latitude={this.state.popupInfo.state.latitude}
        onClose={() => this.setState({popupInfo: null})}
        closeOnClick={true}>
        <p>{"Welcome to RENDEZ-VOUS!"}</p>
      </Popup>
    )
  }render() {
    return (
      <MapGL
        {...viewport}
        mapStyle="mapbox://styles/mapbox/dark-v9"
        mapboxApiAccessToken={TOKEN} >
        {markers}
        {this.renderPopup()}
        <div className="nav" style={navStyle}>
          <NavigationControl/>
          <Marker longitude={-0.1276474} latitude={51.5073219} offsetTop={0} offsetLeft={0}>
            <Pin onClick={() => this.setState({popupInfo: popupInfo})}/>
          </Marker>
        </div>
      </MapGL>
    );
  }
}
